const Ok = (ok) => ({ ok })
const Err = (err) => ({ err })

const map = (f) => (r) => 'ok' in r ? { ok: f(r.ok) } : r
const mapErr = (f) => (r) => 'err' in r ? { err: f(r.err) } : r
const result = (f, d) => (r) => 'ok' in r ? f(r.ok) : d
const fromBool = (b) => (b ? Ok(true) : Err(false))

module.exports = {
  Ok,
  Err,
  map,
  mapErr,
  result,
  fromBool,
}
